        BigDecimal amount = new BigDecimal("100");

        IllegalArgumentException exception = assertThrows(IllegalArgumentException.class, () -> {
            CurrencyConverter.convertCurrency(amount, "INVALID", "USD");
        });

        assertTrue(exception.getMessage().contains("From currency not supported: INVALID"));
    }

    @Test
    public void testConvertCurrencyThrowsExceptionForInvalidToCurrency() {
        BigDecimal amount = new BigDecimal("100");

        IllegalArgumentException exception = assertThrows(IllegalArgumentException.class, () -> {
            CurrencyConverter.convertCurrency(amount, "USD", "INVALID");
        });

        assertTrue(exception.getMessage().contains("To currency not supported: INVALID"));
    }

    @Test
    public void testConvertCurrencyRoundsToTargetCurrencyDecimalPlaces() {
        // Test conversion to JPY (0 decimal places)
        BigDecimal amount = new BigDecimal("100");
        BigDecimal result = CurrencyConverter.convertCurrency(amount, "USD", "JPY");
        assertEquals(0, result.scale());
        assertEquals(new BigDecimal("11000"), result);

        // Test conversion to EUR (2 decimal places)
        result = CurrencyConverter.convertCurrency(amount, "USD", "EUR");
        assertEquals(2, result.scale());
        assertEquals(new BigDecimal("85.00"), result);

        // Test conversion that requires rounding
        BigDecimal preciseAmount = new BigDecimal("33.333333");
        result = CurrencyConverter.convertCurrency(preciseAmount, "USD", "CNY");
        assertEquals(2, result.scale());
        // 33.333333 * 6.5 = 216.666665, rounded to 2 decimal places = 216.67
        assertEquals(new BigDecimal("216.67"), result);
    }

    @Test
    public void testConvertCurrencyRoundingModeHalfUp() {
        // Test that rounding uses HALF_UP mode
        BigDecimal amount = new BigDecimal("1.235");
        BigDecimal result = CurrencyConverter.convertCurrency(amount, "USD", "EUR");
        // 1.235 * 0.85 = 1.04975, rounded to 2 decimal places with HALF_UP = 1.05
        assertEquals(new BigDecimal("1.05"), result);

        amount = new BigDecimal("1.234");
        result = CurrencyConverter.convertCurrency(amount, "USD", "EUR");
        // 1.234 * 0.85 = 1.0489, rounded to 2 decimal places with HALF_UP = 1.05
        assertEquals(new BigDecimal("1.05"), result);
    }
}

