import java.util.Date;
import java.util.HashMap;
import java.util.Map;

class AuthTokenValidator {
    public Map<String, Object> validateToken(String token, String clientIp) {
        Map<String, Object> result = new HashMap<>();
        if (token == null || token.isEmpty()) {
            result.put("valid", false);
            result.put("message", "Token is empty");
            return result;
        }
        if (!token.contains("user")) {
            result.put("valid", false);
            result.put("message", "Token format invalid");
            return result;
        }

        // Extract IP address from token after 'user' substring
        int userIndex = token.indexOf("user");
        String afterUser = token.substring(userIndex + 4); // Skip "user"

        // Find the IP address (assuming it's the next part after 'user')
        String[] parts = afterUser.split("[^0-9.]");
        String tokenIp = null;
        for (String part : parts) {
            if (part.matches("\\d+\\.\\d+\\.\\d+\\.\\d+")) {
                tokenIp = part;
                break;
            }
        }

        if (tokenIp == null || !tokenIp.equals(clientIp)) {
            result.put("valid", false);
            result.put("message", "IP address mismatch");
            return result;
        }

        result.put("valid", true);
        result.put("message", "Validation successful");
        return result;
    }
}

