import java.util.Date;
import java.util.HashMap;
import java.util.Map;

class AuthTokenValidator {
    private Map<String, TokenInfo> tokenDatabase;
    private Map<String, User> userDatabase;

    public AuthTokenValidator() {
        // Initialize token database with tokens having expiration and status
        tokenDatabase = new HashMap<>();
        Date futureDate = new Date(System.currentTimeMillis() + 86400000); // 24 hours from now
        Date pastDate = new Date(System.currentTimeMillis() - 86400000); // 24 hours ago

        tokenDatabase.put("valid_token_user123_192.168.1.1", new TokenInfo("user123", futureDate, 1));
        tokenDatabase.put("expired_token", new TokenInfo("user123", pastDate, 1));
        tokenDatabase.put("invalid_status_token", new TokenInfo("user123", futureDate, 0));
        tokenDatabase.put("nonexistent_user_token", new TokenInfo("user999", futureDate, 1));

        // Initialize user database
        userDatabase = new HashMap<>();
        userDatabase.put("user123", new User("user123", "John Doe"));
    }

    public Map<String, Object> validateToken(String token, String clientIp) {
        Map<String, Object> result = new HashMap<>();
        if (token == null || token.isEmpty()) {
            result.put("valid", false);
            result.put("message", "Token is empty");
            return result;
        }

        // Check if token exists in database
        if (!tokenDatabase.containsKey(token)) {
            result.put("valid", false);
            result.put("message", "Token not found");
            return result;
        }

        TokenInfo tokenInfo = tokenDatabase.get(token);

        // Check token expiration
        Date currentTime = new Date();
        if (tokenInfo.getExpirationTime().before(currentTime)) {
            result.put("valid", false);
            result.put("message", "Token expired");
            return result;
        }

        // Check token status
        if (tokenInfo.getStatus() != 1) {
            result.put("valid", false);
            result.put("message", "Token invalid");
            return result;
        }

        // Check if user exists
        String userId = tokenInfo.getUserId();
        if (!userDatabase.containsKey(userId)) {
            result.put("valid", false);
            result.put("message", "User not found");
            return result;
        }

        // All checks passed
        result.put("valid", true);
        result.put("message", "Validation successful");
        result.put("user", userDatabase.get(userId));
        return result;
    }

    // Helper classes
    private static class TokenInfo {
        private String userId;
        private Date expirationTime;
        private int status;

        public TokenInfo(String userId, Date expirationTime, int status) {
            this.userId = userId;
            this.expirationTime = expirationTime;
            this.status = status;
        }

        public String getUserId() { return userId; }
        public Date getExpirationTime() { return expirationTime; }
        public int getStatus() { return status; }
    }

    private static class User {
        private String id;
        private String name;

        public User(String id, String name) {
            this.id = id;
            this.name = name;
        }

        public String getId() { return id; }
        public String getName() { return name; }
    }
}

