import java.util.Map;
import java.util.LinkedHashMap;
import java.util.List;
import org.json.JSONObject;
import org.json.JSONArray;
import org.json.JSONException;
import java.util.logging.Logger;

class DataStructureAnalyzer {
    private static final Logger logger = Logger.getLogger(DataStructureAnalyzer.class.getName());

    public static Map<String, Object> analyzeDataStructure(Object data) {
        if (data == null) {
            throw new IllegalArgumentException("Input data cannot be null");
        }

        Map<String, Object> result = new LinkedHashMap<>();
        result.put("containsBinary", false);
        result.put("maxDepth", 0);
        result.put("totalElements", 0);

        // Initialize typeCounts map with all required keys set to 0
        Map<String, Integer> typeCounts = new LinkedHashMap<>();
        typeCounts.put("JSONObject", 0);
        typeCounts.put("JSONArray", 0);
        typeCounts.put("String", 0);
        typeCounts.put("Number", 0);
        typeCounts.put("Boolean", 0);
        typeCounts.put("byte[]", 0);
        typeCounts.put("Other", 0);

        result.put("typeCounts", typeCounts);

        analyzeRecursive(data, result, typeCounts, 1);

        return result;
    }

    private static void analyzeRecursive(Object data, Map<String, Object> result, 
                                       Map<String, Integer> typeCounts, int currentDepth) {
        // Increment totalElements for each visited node
        int totalElements = (Integer) result.get("totalElements");
        result.put("totalElements", totalElements + 1);

        // Update maxDepth if current depth exceeds stored value
        int maxDepth = (Integer) result.get("maxDepth");
        if (currentDepth > maxDepth) {
            result.put("maxDepth", currentDepth);
        }

        // Increment counters based on data type
        if (data instanceof String) {
            typeCounts.put("String", typeCounts.get("String") + 1);
        } else if (data instanceof Number) {
            typeCounts.put("Number", typeCounts.get("Number") + 1);
        } else if (data instanceof Boolean) {
            typeCounts.put("Boolean", typeCounts.get("Boolean") + 1);
        } else if (data instanceof byte[]) {
            typeCounts.put("byte[]", typeCounts.get("byte[]") + 1);
            result.put("containsBinary", true);
        } else if (data instanceof JSONObject) {
            typeCounts.put("JSONObject", typeCounts.get("JSONObject") + 1);
            // Recursively analyze JSONObject values with error handling
            JSONObject jsonObj = (JSONObject) data;
            for (String key : jsonObj.keySet()) {
                try {
                    Object value = jsonObj.get(key);
                    if (value != null && value != JSONObject.NULL) {
                        analyzeRecursive(value, result, typeCounts, currentDepth + 1);
                    }
                } catch (JSONException e) {
                    logger.warning("JSONException occurred while accessing JSONObject key '" + key + "': " + e.getMessage());
                }
            }
        } else if (data instanceof JSONArray) {
            typeCounts.put("JSONArray", typeCounts.get("JSONArray") + 1);
            // Recursively analyze JSONArray elements with error handling
            JSONArray jsonArr = (JSONArray) data;
            for (int i = 0; i < jsonArr.length(); i++) {
                try {
                    Object element = jsonArr.get(i);
                    if (element != null && element != JSONObject.NULL) {
                        analyzeRecursive(element, result, typeCounts, currentDepth + 1);
                    }
                } catch (JSONException e) {
                    logger.warning("JSONException occurred while accessing JSONArray index " + i + ": " + e.getMessage());
                }
            }
        } else if (data instanceof Map) {
            typeCounts.put("JSONObject", typeCounts.get("JSONObject") + 1);
            // Recursively analyze map values
            for (Object value : ((Map<?, ?>) data).values()) {
                analyzeRecursive(value, result, typeCounts, currentDepth + 1);
            }
        } else if (data instanceof List) {
            typeCounts.put("JSONArray", typeCounts.get("JSONArray") + 1);
            // Recursively analyze list elements
            for (Object element : (List<?>) data) {
                analyzeRecursive(element, result, typeCounts, currentDepth + 1);
            }
        } else {
            typeCounts.put("Other", typeCounts.get("Other") + 1);
        }
    }
}

