import java.util.*;

class EnhancedBinarySearchTree<T extends Comparable<T>> {
    private Node root;
    private int size;

    private class Node {
        T data;
        Node left, right;

        Node(T data) {
            this.data = data;
        }
    }

    public boolean add(T element) {
        if (element == null) {
            throw new NullPointerException("Cannot add null elements");
        }

        if (root == null) {
            root = new Node(element);
            size++;
            return true;
        }

        return add(root, element);
    }

    private boolean add(Node node, T element) {
        int cmp = element.compareTo(node.data);

        if (cmp < 0) {
            if (node.left == null) {
                node.left = new Node(element);
                size++;
                return true;
            } else {
                return add(node.left, element);
            }
        } else if (cmp > 0) {
            if (node.right == null) {
                node.right = new Node(element);
                size++;
                return true;
            } else {
                return add(node.right, element);
            }
        } else {
            return false;
        }
    }

    public boolean contains(T element) {
        return contains(root, element);
    }

    private boolean contains(Node node, T element) {
        if (node == null) return false;

        int cmp = element.compareTo(node.data);
        if (cmp < 0) {
            return contains(node.left, element);
        } else if (cmp > 0) {
            return contains(node.right, element);
        } else {
            return true;
        }
    }

    public T first() {
        if (root == null) {
            throw new NoSuchElementException("Tree is empty");
        }

        Node current = root;
        while (current.left != null) {
            current = current.left;
        }
        return current.data;
    }

    public T last() {
        if (root == null) {
            throw new NoSuchElementException("Tree is empty");
        }

        Node current = root;
        while (current.right != null) {
            current = current.right;
        }
        return current.data;
    }

    public boolean remove(T element) {
        if (element == null) {
            throw new NullPointerException();
        }

        int initialSize = size;
        root = remove(root, element);
        return size < initialSize;
    }

    private Node remove(Node node, T element) {
        if (node == null) {
            return null;
        }

        int cmp = element.compareTo(node.data);

        if (cmp < 0) {
            node.left = remove(node.left, element);
        } else if (cmp > 0) {
            node.right = remove(node.right, element);
        } else {
            // Found the node to remove
            size--;

            // Case 1: Node with no children
            if (node.left == null && node.right == null) {
                return null;
            }

            // Case 2: Node with one child
            if (node.left == null) {
                return node.right;
            }
            if (node.right == null) {
                return node.left;
            }

            // Case 3: Node with two children
            // Find in-order successor (smallest in right subtree)
            Node successor = findMin(node.right);
            node.data = successor.data;
            node.right = remove(node.right, successor.data);
            size++; // Compensate for the extra decrement in recursive call
        }

        return node;
    }

    private Node findMin(Node node) {
        while (node.left != null) {
            node = node.left;
        }
        return node;
    }
}

