        assertTrue(tree.contains(10));
        assertTrue(tree.contains(15));
    }

    @Test
    void testRemoveNodeWithOneChild() {
        EnhancedBinarySearchTree<Integer> tree = new EnhancedBinarySearchTree<>();
        tree.add(10);
        tree.add(5);
        tree.add(3);

        assertTrue(tree.remove(5));
        assertFalse(tree.contains(5));
        assertTrue(tree.contains(10));
        assertTrue(tree.contains(3));
    }

    @Test
    void testRemoveNodeWithTwoChildren() {
        EnhancedBinarySearchTree<Integer> tree = new EnhancedBinarySearchTree<>();
        tree.add(10);
        tree.add(5);
        tree.add(15);
        tree.add(3);
        tree.add(7);
        tree.add(12);
        tree.add(18);

        assertTrue(tree.remove(10)); // Root with two children
        assertFalse(tree.contains(10));

        // Verify BST property is maintained
        assertEquals(Integer.valueOf(3), tree.first());
        assertEquals(Integer.valueOf(18), tree.last());

        // All other elements should still be present
