    void testIsEmptyOnNewTree() {
        EnhancedBinarySearchTree<Integer> tree = new EnhancedBinarySearchTree<>();
        assertTrue(tree.isEmpty());
    }

    @Test
    void testIsEmptyAfterAddingElements() {
        EnhancedBinarySearchTree<Integer> tree = new EnhancedBinarySearchTree<>();
        tree.add(5);
        assertFalse(tree.isEmpty());
    }

    @Test
    void testSizeOnNewTree() {
        EnhancedBinarySearchTree<Integer> tree = new EnhancedBinarySearchTree<>();
        assertEquals(0, tree.size());
    }

    @Test
    void testSizeAfterAddingElements() {
        EnhancedBinarySearchTree<Integer> tree = new EnhancedBinarySearchTree<>();
        tree.add(5);
        tree.add(3);
        tree.add(7);
        assertEquals(3, tree.size());
    }

    @Test
    void testSizeAfterRemovingElements() {
        EnhancedBinarySearchTree<Integer> tree = new EnhancedBinarySearchTree<>();
        tree.add(5);
        tree.add(3);
        tree.add(7);
        tree.remove(3);
        assertEquals(2, tree.size());
    }

    @Test
    void testClearEmptyTree() {
        EnhancedBinarySearchTree<Integer> tree = new EnhancedBinarySearchTree<>();
        tree.clear();
        assertTrue(tree.isEmpty());
        assertEquals(0, tree.size());
    }

    @Test
    void testClearNonEmptyTree() {
        EnhancedBinarySearchTree<Integer> tree = new EnhancedBinarySearchTree<>();
        tree.add(5);
        tree.add(3);
        tree.add(7);
        tree.add(1);
        tree.add(9);

        tree.clear();

        assertTrue(tree.isEmpty());
        assertEquals(0, tree.size());
        assertFalse(tree.contains(5));
        assertFalse(tree.contains(3));
        assertFalse(tree.contains(7));
    }

    @Test
    void testFirstAndLastAfterClear() {
        EnhancedBinarySearchTree<Integer> tree = new EnhancedBinarySearchTree<>();
        tree.add(5);
        tree.add(3);
        tree.add(7);

        tree.clear();

        assertThrows(NoSuchElementException.class, () -> tree.first());
        assertThrows(NoSuchElementException.class, () -> tree.last());
    }

    @Test
    void testIsEmptyAfterClear() {
        EnhancedBinarySearchTree<Integer> tree = new EnhancedBinarySearchTree<>();
        tree.add(10);
        assertFalse(tree.isEmpty());

        tree.clear();
        assertTrue(tree.isEmpty());
    }
}

