import java.util.List;
import java.util.Arrays;

class TestAdvancedBST {

    @Test
    void testPreorderTraversal() {
        AdvancedBST bst = new AdvancedBST();
        bst.insert(5);
        bst.insert(3);
        bst.insert(7);
        bst.insert(2);
        bst.insert(4);
        bst.insert(6);
        bst.insert(8);

        List<Integer> preorderResult = bst.preorder();
        List<Integer> expected = Arrays.asList(5, 3, 2, 4, 7, 6, 8);
        assertEquals(expected, preorderResult);
    }

    @Test
    void testPostorderTraversal() {
        AdvancedBST bst = new AdvancedBST();
        bst.insert(5);
        bst.insert(3);
        bst.insert(7);
        bst.insert(2);
        bst.insert(4);
        bst.insert(6);
        bst.insert(8);

        List<Integer> postorderResult = bst.postorder();
        List<Integer> expected = Arrays.asList(2, 4, 3, 6, 8, 7, 5);
        assertEquals(expected, postorderResult);
    }

    @Test
    void testPreorderEmptyTree() {
        AdvancedBST bst = new AdvancedBST();
        List<Integer> preorderResult = bst.preorder();
        assertTrue(preorderResult.isEmpty());
    }

    @Test
    void testPostorderEmptyTree() {
        AdvancedBST bst = new AdvancedBST();
        List<Integer> postorderResult = bst.postorder();
        assertTrue(postorderResult.isEmpty());
    }

    @Test
    void testPreorderSingleNode() {
        AdvancedBST bst = new AdvancedBST();
        bst.insert(10);

        List<Integer> preorderResult = bst.preorder();
        List<Integer> expected = Arrays.asList(10);
        assertEquals(expected, preorderResult);
    }

    @Test
    void testPostorderSingleNode() {
        AdvancedBST bst = new AdvancedBST();
        bst.insert(10);

        List<Integer> postorderResult = bst.postorder();
        List<Integer> expected = Arrays.asList(10);
        assertEquals(expected, postorderResult);
    }

    @Test
    void testTraversalOrdersDifferent() {
        AdvancedBST bst = new AdvancedBST();
        bst.insert(5);
        bst.insert(3);
        bst.insert(7);

        List<Integer> inorderResult = bst.inorder();
        List<Integer> preorderResult = bst.preorder();
        List<Integer> postorderResult = bst.postorder();

        // Verify that all three traversals produce different orders
        assertNotEquals(inorderResult, preorderResult);
        assertNotEquals(inorderResult, postorderResult);
        assertNotEquals(preorderResult, postorderResult);

        // Verify specific orders
        assertEquals(Arrays.asList(3, 5, 7), inorderResult);
        assertEquals(Arrays.asList(5, 3, 7), preorderResult);
        assertEquals(Arrays.asList(3, 7, 5), postorderResult);
    }
}

