import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;

class TestAdvancedBST {

    @Test
    void testHeightEmptyTree() {
        AdvancedBST bst = new AdvancedBST();
        assertEquals(-1, bst.height());
    }

    @Test
    void testHeightSingleNode() {
        AdvancedBST bst = new AdvancedBST();
        bst.insert(10);
        assertEquals(0, bst.height());
    }

    @Test
    void testHeightTwoLevels() {
        AdvancedBST bst = new AdvancedBST();
        bst.insert(10);
        bst.insert(5);
        assertEquals(1, bst.height());
    }

    @Test
    void testHeightBalancedTree() {
        AdvancedBST bst = new AdvancedBST();
        bst.insert(10);
        bst.insert(5);
        bst.insert(15);
        bst.insert(3);
        bst.insert(7);
        assertEquals(2, bst.height());
    }

    @Test
    void testHeightUnbalancedTree() {
        AdvancedBST bst = new AdvancedBST();
        bst.insert(10);
        bst.insert(20);
        bst.insert(30);
        bst.insert(40);
        assertEquals(3, bst.height());
    }

    @Test
    void testHeightAfterRemoval() {
        AdvancedBST bst = new AdvancedBST();
        bst.insert(10);
        bst.insert(5);
        bst.insert(15);
        bst.insert(3);
        assertEquals(2, bst.height());

        bst.remove(3);
        assertEquals(1, bst.height());

        bst.remove(5);
        bst.remove(15);
        assertEquals(0, bst.height());
    }
}

