import java.util.List;

class TestTicTacToeGame {

    @Test
    public void testRowWinDetection() {
        TicTacToeGame game = new TicTacToeGame(3);

        // X wins by filling first row
        assertTrue(game.makeMove(0, 0)); // X
        assertTrue(game.makeMove(1, 0)); // O
        assertTrue(game.makeMove(0, 1)); // X
        assertTrue(game.makeMove(1, 1)); // O
        assertTrue(game.makeMove(0, 2)); // X - should win

        assertTrue(game.isGameOver());
        assertEquals('X', game.getWinner());
    }

    @Test
    public void testColumnWinDetection() {
        TicTacToeGame game = new TicTacToeGame(3);

        // O wins by filling first column
        assertTrue(game.makeMove(1, 1)); // X
        assertTrue(game.makeMove(0, 0)); // O
        assertTrue(game.makeMove(1, 2)); // X
        assertTrue(game.makeMove(1, 0)); // O
        assertTrue(game.makeMove(2, 2)); // X
        assertTrue(game.makeMove(2, 0)); // O - should win

        assertTrue(game.isGameOver());
        assertEquals('O', game.getWinner());
    }

    @Test
    public void testNoMovesAfterWin() {
        TicTacToeGame game = new TicTacToeGame(3);

        // X wins by filling first row
        game.makeMove(0, 0); // X
        game.makeMove(1, 0); // O
        game.makeMove(0, 1); // X
        game.makeMove(1, 1); // O
        game.makeMove(0, 2); // X - wins

        // Try to make another move - should fail
        assertFalse(game.makeMove(2, 2));
    }

    @Test
    public void testGameNotOverWithoutWin() {
        TicTacToeGame game = new TicTacToeGame(3);

        game.makeMove(0, 0); // X
        game.makeMove(0, 1); // O
        game.makeMove(1, 0); // X

        assertFalse(game.isGameOver());
        assertEquals('-', game.getWinner());
    }
}

