import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.List;

class TestTicTacToeGame {

    @Test
    public void testMainDiagonalWin() {
        TicTacToeGame game = new TicTacToeGame(3);

        // X wins on main diagonal
        assertTrue(game.makeMove(0, 0)); // X
        assertTrue(game.makeMove(0, 1)); // O
        assertTrue(game.makeMove(1, 1)); // X
        assertTrue(game.makeMove(0, 2)); // O
        assertTrue(game.makeMove(2, 2)); // X wins

        assertTrue(game.isGameOver());
        assertEquals('X', game.getWinner());
        assertFalse(game.isDraw());
    }

    @Test
    public void testAntiDiagonalWin() {
        TicTacToeGame game = new TicTacToeGame(3);

        // O wins on anti-diagonal
        assertTrue(game.makeMove(0, 0)); // X
        assertTrue(game.makeMove(0, 2)); // O
        assertTrue(game.makeMove(1, 0)); // X
        assertTrue(game.makeMove(1, 1)); // O
        assertTrue(game.makeMove(2, 1)); // X
        assertTrue(game.makeMove(2, 0)); // O wins

        assertTrue(game.isGameOver());
        assertEquals('O', game.getWinner());
        assertFalse(game.isDraw());
    }

    @Test
    public void testDrawDetection() {
        TicTacToeGame game = new TicTacToeGame(3);

        // Create a proper draw scenario: X O X / O O X / X X O
        assertTrue(game.makeMove(0, 0)); // X
        assertTrue(game.makeMove(0, 1)); // O
        assertTrue(game.makeMove(0, 2)); // X
        assertTrue(game.makeMove(1, 0)); // O
        assertTrue(game.makeMove(2, 1)); // X
        assertTrue(game.makeMove(1, 1)); // O
        assertTrue(game.makeMove(2, 0)); // X
        assertTrue(game.makeMove(2, 2)); // O
        assertTrue(game.makeMove(1, 2)); // X - board full, no winner

        assertTrue(game.isGameOver());
        assertEquals('-', game.getWinner());
        assertTrue(game.isDraw());
    }

    @Test
    public void testDiagonalOnlyCheckedWhenMoveIsOnDiagonal() {
        TicTacToeGame game = new TicTacToeGame(3);

        // Place X on main diagonal positions but make move off diagonal
        game.makeMove(0, 0); // X
        game.makeMove(0, 1); // O
        game.makeMove(1, 1); // X
        game.makeMove(0, 2); // O
        // Don't complete diagonal yet
        game.makeMove(1, 0); // X (off diagonal)

        // Game should not be over yet
        assertFalse(game.isGameOver());
        assertEquals('-', game.getWinner());
    }

    @Test
    public void testNoMoveAfterDraw() {
        TicTacToeGame game = new TicTacToeGame(3);

        // Create a draw scenario: X O X / O O X / X X O
        game.makeMove(0, 0); // X
        game.makeMove(0, 1); // O
        game.makeMove(0, 2); // X
        game.makeMove(1, 0); // O
        game.makeMove(2, 1); // X
        game.makeMove(1, 1); // O
        game.makeMove(2, 0); // X
        game.makeMove(2, 2); // O
        game.makeMove(1, 2); // X - draw

        assertTrue(game.isDraw());

        // Should not be able to make more moves
        assertFalse(game.makeMove(0, 0));
    }
}

