import java.util.ArrayList;
import java.util.List;

class TicTacToeGame {
    private char[][] board;
    private char currentPlayer;
    private int size;
    private boolean gameOver;
    private char winner;
    private boolean isDraw;

    public TicTacToeGame(int size) {
        if (size < 3) {
            throw new IllegalArgumentException("Size must be at least 3");
        }
        this.size = size;
        this.board = new char[size][size];
        for (int i = 0; i < size; i++) {
            for (int j = 0; j < size; j++) {
                board[i][j] = '-';
            }
        }
        this.currentPlayer = 'X';
        this.gameOver = false;
        this.winner = '-';
        this.isDraw = false;
    }

    public boolean makeMove(int row, int col) {
        // Check if game is over
        if (gameOver) {
            return false;
        }

        // Check bounds
        if (row < 0 || row >= size || col < 0 || col >= size) {
            return false;
        }

        if (board[row][col] != '-') return false;

        char playerMakingMove = currentPlayer;
        board[row][col] = currentPlayer;

        // Check for win after placing the move
        if (checkWin(playerMakingMove, row, col)) {
            gameOver = true;
            winner = playerMakingMove;
        } else if (isBoardFull()) {
            // Check for draw if no winner and board is full
            gameOver = true;
            isDraw = true;
        }

        currentPlayer = (currentPlayer == 'X') ? 'O' : 'X';
        return true;
    }

    private boolean checkWin(char player, int lastRow, int lastCol) {
        // Check the row of the last move
        boolean rowWin = true;
        for (int j = 0; j < size; j++) {
            if (board[lastRow][j] != player) {
                rowWin = false;
                break;
            }
        }
        if (rowWin) return true;

        // Check the column of the last move
        boolean colWin = true;
        for (int i = 0; i < size; i++) {
            if (board[i][lastCol] != player) {
                colWin = false;
                break;
            }
        }
        if (colWin) return true;

        // Check main diagonal (top-left to bottom-right) if move is on it
        if (lastRow == lastCol) {
            boolean mainDiagWin = true;
            for (int i = 0; i < size; i++) {
                if (board[i][i] != player) {
                    mainDiagWin = false;
                    break;
                }
            }
            if (mainDiagWin) return true;
        }

        // Check anti-diagonal (top-right to bottom-left) if move is on it
        if (lastRow + lastCol == size - 1) {
            boolean antiDiagWin = true;
            for (int i = 0; i < size; i++) {
                if (board[i][size - 1 - i] != player) {
                    antiDiagWin = false;
                    break;
                }
            }
            if (antiDiagWin) return true;
        }

        return false;
    }

    private boolean isBoardFull() {
        for (int i = 0; i < size; i++) {
            for (int j = 0; j < size; j++) {
                if (board[i][j] == '-') {
                    return false;
                }
            }
        }
        return true;
    }

    public List<String> getBoardState() {
        List<String> state = new ArrayList<>();
        for (char[] row : board) {
            state.add(new String(row));
        }
        return state;
    }

    public char getCurrentPlayer() {
        return currentPlayer;
    }

    public boolean isGameOver() {
        return gameOver;
    }

    public char getWinner() {
        return winner;
    }

    public boolean isDraw() {
        return isDraw;
    }

    public String getGameStatus() {
        if (gameOver) {
            if (winner == 'X') {
                return "X wins";
            } else if (winner == 'O') {
                return "O wins";
            } else if (isDraw) {
                return "Draw";
            }
        }
        return "In progress";
    }
}

