import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;

class TestTicTacToeGame {

    @Test
    void testGetGameStatusXWins() {
        TicTacToeGame game = new TicTacToeGame(3);
        // X wins by filling first row
        game.makeMove(0, 0); // X
        game.makeMove(1, 0); // O
        game.makeMove(0, 1); // X
        game.makeMove(1, 1); // O
        game.makeMove(0, 2); // X wins

        assertEquals("X wins", game.getGameStatus());
    }

    @Test
    void testGetGameStatusOWins() {
        TicTacToeGame game = new TicTacToeGame(3);
        // O wins by filling first column
        game.makeMove(1, 1); // X
        game.makeMove(0, 0); // O
        game.makeMove(0, 1); // X
        game.makeMove(1, 0); // O
        game.makeMove(2, 2); // X
        game.makeMove(2, 0); // O wins

        assertEquals("O wins", game.getGameStatus());
    }

    @Test
    void testGetGameStatusDraw() {
        TicTacToeGame game = new TicTacToeGame(3);
        // Create a draw scenario
        game.makeMove(0, 0); // X
        game.makeMove(0, 1); // O
        game.makeMove(0, 2); // X
        game.makeMove(1, 0); // O
        game.makeMove(1, 1); // X
        game.makeMove(2, 0); // O
        game.makeMove(1, 2); // X
        game.makeMove(2, 2); // O
        game.makeMove(2, 1); // X - board full, no winner

        assertEquals("Draw", game.getGameStatus());
    }

    @Test
    void testGetGameStatusInProgress() {
        TicTacToeGame game = new TicTacToeGame(3);
        // Game just started
        assertEquals("In progress", game.getGameStatus());

        // After some moves but no winner yet
        game.makeMove(0, 0); // X
        game.makeMove(1, 1); // O
        assertEquals("In progress", game.getGameStatus());
    }

    @Test
    void testConstructorThrowsExceptionForSizeLessThan3() {
        assertThrows(IllegalArgumentException.class, () -> {
            new TicTacToeGame(2);
        });

        assertThrows(IllegalArgumentException.class, () -> {
            new TicTacToeGame(1);
        });

        assertThrows(IllegalArgumentException.class, () -> {
            new TicTacToeGame(0);
        });
    }

    @Test
    void testConstructorAcceptsSizeThreeAndAbove() {
        // Should not throw exception
        assertDoesNotThrow(() -> {
            new TicTacToeGame(3);
        });

        assertDoesNotThrow(() -> {
            new TicTacToeGame(4);
        });

        assertDoesNotThrow(() -> {
            new TicTacToeGame(5);
        });
    }
}
