public static List<Document> loadDocuments(Path directoryPath, DocumentParser documentParser) {
    if (!isDirectory(directoryPath)) {
        throw illegalArgument("'%s' is not a directory", directoryPath);
    }

    try (Stream<Path> pathStream = Files.list(directoryPath)) {
        List<Document> documents = new ArrayList<>();
        pathStream.filter(path -> !Files.isDirectory(path))
                .forEach(path -> {
                    try {
                        Document document = loadDocument(path, documentParser);
                        documents.add(document);
                    } catch (Exception e) {
                        // Skip files that fail to parse
                    }
                });
        return documents;
    } catch (IOException e) {
        throw new RuntimeException("Runtime error", e);
    }
}

