void should_calculate_cosine_similarity() {
    Embedding embeddingA = Embedding.from(new float[] {1, -1, 1});
    Embedding embeddingB = Embedding.from(new float[] {-1, 1, -1});
    Embedding zeroEmbedding = Embedding.from(new float[] {0, 0, 0});
    Embedding nonZeroEmbedding = Embedding.from(new float[] {1, 2, 3});
    Embedding nearZeroEmbedding = Embedding.from(new float[] {1e-10f, 1e-10f, 1e-10f});

    assertThat(CosineSimilarity.between(embeddingA, embeddingA)).isCloseTo(1, withPercentage(1));
    assertThat(CosineSimilarity.between(embeddingA, embeddingB)).isCloseTo(-1, withPercentage(1));

    // Test zero vector handling - should return exactly 0.0
    assertThat(CosineSimilarity.between(zeroEmbedding, zeroEmbedding)).isEqualTo(0.0);
    assertThat(CosineSimilarity.between(zeroEmbedding, nonZeroEmbedding)).isEqualTo(0.0);
    assertThat(CosineSimilarity.between(nonZeroEmbedding, zeroEmbedding)).isEqualTo(0.0);

    // Test near-zero vector handling - should return 0.0 due to epsilon threshold
    assertThat(CosineSimilarity.between(nearZeroEmbedding, nearZeroEmbedding)).isEqualTo(0.0);
    assertThat(CosineSimilarity.between(nearZeroEmbedding, nonZeroEmbedding)).isEqualTo(0.0);
}

