    String result1 = StringUtil.underlineToCamel("a_b");
    Assert.assertEquals("aB", result1);

    // Multiple underscores - previous code would handle this the same way
    String result2 = StringUtil.underlineToCamel("hello_world_test");
    Assert.assertEquals("helloWorldTest", result2);

    // Test that would expose difference: input starting with uppercase
    // Previous code converts to lowercase first, current code doesn't
    String result3 = StringUtil.underlineToCamel("Hello_world");
    Assert.assertEquals("HelloWorld", result3);

    // Test empty string behavior difference
    String result4 = StringUtil.underlineToCamel("");
    Assert.assertEquals("", result4);
}

turn_datas.solution:
public static String underlineToCamel(String string) {
    if (noText(string)) {
        return "";
    }
    if (Character.isUpperCase(string.charAt(0))) {
        string = string.toLowerCase();
    }
    int strLen = string.length();
    StringBuilder sb = new StringBuilder(strLen);
    for (int i = 0; i < strLen; i++) {
        char c = string.charAt(i);
        if (c == '_') {
            // Skip all consecutive underscores
            while (i + 1 < strLen && string.charAt(i + 1) == '_') {
                i++;
            }
            // If there's a character after the underscore(s), capitalize it
            if (i + 1 < strLen) {
                i++;
                sb.append(Character.toUpperCase(string.charAt(i)));
            }
        } else {
            sb.append(c);
        }
    }
    return sb.toString();
}

