public static <I, S extends I, T extends S> T copyToTarget(S source, Class<I> sourceInterface, Class<T> targetClass) {
    if (source == null) {
        return null;
    }

    try {
        T target = targetClass.getConstructor().newInstance();

        // Copy all fields from source to target using reflection
        Field[] fields = source.getClass().getDeclaredFields();
        for (Field field : fields) {
            field.setAccessible(true);
            Object value = field.get(source);

            // Find corresponding field in target class
            try {
                Field targetField = targetClass.getDeclaredField(field.getName());
                targetField.setAccessible(true);
                targetField.set(target, value);
            } catch (NoSuchFieldException e) {
                // Field doesn't exist in target, skip it
            }
        }

        return target;
    } catch (Exception e) {
        throw new RuntimeException("Failed to copy to target", e);
    }
}

