public static <I, S extends I, T extends S> T copyToTarget(S source, Class<I> sourceInterface, Class<T> targetClass) {
    if (source == null) {
        return null;
    }

    try {
        T target = targetClass.getConstructor().newInstance();

        // Get all methods from the source interface
        java.lang.reflect.Method[] interfaceMethods = sourceInterface.getDeclaredMethods();

        for (java.lang.reflect.Method method : interfaceMethods) {
            // Check if method is a getter (starts with "get" and has no parameters)
            if (method.getName().startsWith("get") && method.getParameterCount() == 0) {
                // Get the property name from getter method
                String propertyName = method.getName().substring(3);
                propertyName = Character.toLowerCase(propertyName.charAt(0)) + propertyName.substring(1);

                // Get value from source using the getter
                Object value = method.invoke(source);

                // Find corresponding field in target class and set it
                try {
                    java.lang.reflect.Field targetField = targetClass.getDeclaredField(propertyName);
                    targetField.setAccessible(true);
                    targetField.set(target, value);
                } catch (NoSuchFieldException e) {
                    // Field doesn't exist in target, skip it
                }
            }
        }

        return target;
    } catch (Exception e) {
        throw new RuntimeException("Failed to copy to target", e);
    }
}

