@Test
public void mergeBeans() {

	var portableOptions = new TestPortableOptionsImpl();
	portableOptions.setName("John");
	portableOptions.setAge(30);
	portableOptions.setNonInterfaceField("NonInterfaceField");

	var specificOptions = new TestSpecificOptions();
	specificOptions.setName("Mike");
	specificOptions.setAge(60);
	specificOptions.setSpecificField("SpecificField");

	// Test the NEW fourth parameter: overrideNonNullTargetValues = false
	// This should preserve existing non-null target values
	TestSpecificOptions resultWithFalseFlag = ModelOptionsUtils.mergeBeans(portableOptions, specificOptions,
			TestPortableOptions.class, false);

	// With false flag, non-null target values should NOT be overridden
	assertThat(resultWithFalseFlag.getAge()).isEqualTo(60); // Target's Mike's age preserved
	assertThat(resultWithFalseFlag.getName()).isEqualTo("Mike"); // Target's Mike's name preserved
	assertThat(resultWithFalseFlag.getSpecificField()).isEqualTo("SpecificField");

	// Reset for next test
	specificOptions.setName("Mike");
	specificOptions.setAge(60);

	// Test the NEW fourth parameter: overrideNonNullTargetValues = true
	// This should allow source to overwrite non-null target values
	TestSpecificOptions resultWithTrueFlag = ModelOptionsUtils.mergeBeans(portableOptions, specificOptions,
			TestPortableOptions.class, true);

	// With true flag, non-null target values SHOULD be overridden by source
	assertThat(resultWithTrueFlag.getAge()).isEqualTo(30); // John's age overwrites Mike's
	assertThat(resultWithTrueFlag.getName()).isEqualTo("John"); // John's name overwrites Mike's
	assertThat(resultWithTrueFlag.getSpecificField()).isEqualTo("SpecificField");
}

