@Test
public void mergeBeans() {

	var portableOptions = new TestPortableOptionsImpl();
	portableOptions.setName("John");
	portableOptions.setAge(30);
	portableOptions.setNonInterfaceField("NonInterfaceField");

	var specificOptions = new TestSpecificOptions();

	specificOptions.setName("Mike");
	specificOptions.setAge(60);
	specificOptions.setSpecificField("SpecificField");

	// Test normal merge behavior - properties not in BEAN_MERGE_FIELD_EXCISIONS should be merged
	TestSpecificOptions result = ModelOptionsUtils.mergeBeans(portableOptions, specificOptions,
			TestPortableOptions.class, true);

	// Verify merge behavior - both name and age should be merged since they're not in exclusion list
	assertThat(result.getName()).isEqualTo("John"); // Should be merged from source
	assertThat(result.getAge()).isEqualTo(30); // Should be merged from source
	assertThat(result.getSpecificField()).isEqualTo("SpecificField");

	// Test null source exception
	assertThatThrownBy(() -> ModelOptionsUtils.mergeBeans(null, specificOptions, TestPortableOptions.class, false))
			.isInstanceOf(IllegalArgumentException.class)
			.hasMessage("Source object must not be null");

	// Test null target exception
	assertThatThrownBy(() -> ModelOptionsUtils.mergeBeans(portableOptions, null, TestPortableOptions.class, false))
			.isInstanceOf(IllegalArgumentException.class)
			.hasMessage("Target object must not be null");
}

