@Test
public void testPrevSetBit() {
    // Test that ensureCapacity properly initializes newly allocated words
    FixedBitSet original = new FixedBitSet(64); // 1 word (64 bits)
    original.set(0);
    original.set(63); // Set the last bit of the first word

    // Ensure capacity to 128 bits (2 words) - this should trigger array growth
    FixedBitSet expanded = FixedBitSet.ensureCapacity(original, 128);

    // Verify that original bits are preserved
    assertTrue("Original bit 0 should be preserved", expanded.get(0));
    assertTrue("Original bit 63 should be preserved", expanded.get(63));

    // Verify that newly allocated bits are properly initialized to zero
    // Test several positions in the newly allocated word
    assertFalse("Bit 64 should be initialized to zero", expanded.get(64));
    assertFalse("Bit 65 should be initialized to zero", expanded.get(65));
    assertFalse("Bit 100 should be initialized to zero", expanded.get(100));
    assertFalse("Bit 127 should be initialized to zero", expanded.get(127));

    // Test that the capacity was actually increased
    assertTrue("Capacity should be at least 128 bits", expanded.length() >= 128);
}

