public float rerank(int topK, ScoreFunction.ExactScoreFunction reranker, float rerankFloor, NodeQueue reranked, NodesUnsorted unused) {
    // Compute exact scores for all items
    int[] ids = new int[size()];
    float[] exactScores = new float[size()];
    float[] approximateScores = new float[size()];

    for (int i = 0; i < size(); i++) {
        long heapValue = heap.get(i + 1);
        ids[i] = decodeNodeId(heapValue);
        approximateScores[i] = decodeScore(heapValue);
        exactScores[i] = reranker.similarityTo(ids[i]);
    }

    // Find topK items with highest exact scores using selection approach
    boolean[] selected = new boolean[size()];
    int selectedCount = 0;

    // Select topK items with highest exact scores
    for (int k = 0; k < topK && k < size(); k++) {
        int bestIdx = -1;
        float bestScore = Float.NEGATIVE_INFINITY;

        for (int i = 0; i < size(); i++) {
            if (!selected[i] && exactScores[i] > bestScore) {
                bestScore = exactScores[i];
                bestIdx = i;
            }
        }

        if (bestIdx >= 0) {
            selected[bestIdx] = true;
            reranked.push(ids[bestIdx], exactScores[bestIdx]);
            selectedCount++;
        }
    }

    // Add remaining items to unused
    for (int i = 0; i < size(); i++) {
        if (!selected[i]) {
            unused.add(ids[i], approximateScores[i]);
        }
    }

    // Return worst approximate score among topK
    if (selectedCount < topK) {
        return Float.POSITIVE_INFINITY;
    }

    float worstApproximate = Float.POSITIVE_INFINITY;
    for (int i = 0; i < size(); i++) {
        if (selected[i]) {
            worstApproximate = Math.min(worstApproximate, approximateScores[i]);
        }
    }

    return worstApproximate;
}

