public int insertSorted(int newNode, float newScore) {
    if (size == nodes.length) {
        growArrays();
    }

    // Find insertion point for ascending order
    int insertPos = 0;
    while (insertPos < size && scores[insertPos] < newScore) {
        insertPos++;
    }

    // Check if node already exists at this position
    if (insertPos < size && nodes[insertPos] == newNode && scores[insertPos] == newScore) {
        return -1; // Node already exists
    }

    // Shift elements to make room
    for (int i = size; i > insertPos; i--) {
        nodes[i] = nodes[i - 1];
        scores[i] = scores[i - 1];
    }

    // Insert new node and score
    nodes[insertPos] = newNode;
    scores[insertPos] = newScore;
    size++;

    return insertPos;
}

