public void testMergeCandidatesSimple() {
  // Test that verifies size is updated correctly after retain operation
  var nodeArray = new NodeArray(5);
  nodeArray.addInOrder(5, 5.0f);
  nodeArray.addInOrder(4, 4.0f);
  nodeArray.addInOrder(3, 3.0f);
  nodeArray.addInOrder(2, 2.0f);
  nodeArray.addInOrder(1, 1.0f);

  // Create a Bits object that selects only the first 2 elements
  var selected = new FixedBitSet(5);
  selected.set(0);
  selected.set(1);

  // Retain only first 2 elements
  nodeArray.retain(selected);

  // Verify size is updated correctly to match retained elements
  assertEquals(2, nodeArray.size());

  // Verify retained elements are correct
  assertArrayEquals(new int[] {5, 4}, nodeArray.copyDenseNodes());
  assertArrayEquals(new float[] {5.0f, 4.0f}, nodeArray.copyDenseScores(), 0.0f);

