    testMarkingAllNodesAsDeleted(true);

    // Verify that the synchronized method maintains thread safety
    // by testing that concurrent access doesn't cause issues
    Thread t1 = new Thread(() -> testMarkingAllNodesAsDeleted(false));
    Thread t2 = new Thread(() -> testMarkingAllNodesAsDeleted(true));

    t1.start();
    t2.start();

    try {
        t1.join();
        t2.join();
    } catch (InterruptedException e) {
