        // verify that vectors written out of order can be read back in correct positions
        for (int ordinal : new int[]{1, 2, 5, 8}) {
            var vector = view.getVector(ordinal);
            assertNotNull(vector);
            assertEquals(256, vector.length());
            // verify the vector is not zero (indicating data was written)
            assertTrue(vector.length() > 0);
        }
    }
}

