@Test
public void testIncrementalWrites() throws IOException {
    var graph = new TestUtil.RandomlyConnectedGraphIndex(50, 16, getRandom());
    var vectors = TestUtil.createRandomVectors(50, 128);
    var ravv = new ListRandomAccessVectorValues(vectors, 128);

    var testPath = testDirectory.resolve("sync_test");

    try (var writer = new OnDiskGraphIndexWriter.Builder(graph, testPath)
            .with(new InlineVectors(ravv.dimension()))
            .build())
    {
        // Test synchronization by verifying execution order
        var executionOrder = new java.util.concurrent.ConcurrentLinkedQueue<Integer>();
        var startLatch = new java.util.concurrent.CountDownLatch(1);
        var threads = new Thread[3];
        var exceptions = new Exception[3];

        for (int t = 0; t < threads.length; t++) {
            final int threadId = t;
            threads[t] = new Thread(() -> {
                try {
                    startLatch.await(); // Ensure all threads start simultaneously
                    executionOrder.add(threadId);
                    // Add a small delay to make synchronization issues more apparent
                    Thread.sleep(10);
                    var state = Feature.singleState(FeatureId.INLINE_VECTORS, new InlineVectors.State(ravv.getVector(threadId)));
                    writer.writeInline(threadId, state);
                    executionOrder.add(threadId + 100); // Mark completion
                } catch (Exception e) {
                    exceptions[threadId] = e;
                }
            });
        }

        for (var thread : threads) {
            thread.start();
        }

        startLatch.countDown(); // Start all threads simultaneously

        for (var thread : threads) {
            thread.join();
        }

        // Verify no exceptions occurred
        for (var exception : exceptions) {
            if (exception != null) {
                throw new AssertionError("Concurrent access failed", exception);
            }
        }

        // Verify that execution was properly serialized
        // With proper synchronization, we should see complete execution blocks
        var orderList = new java.util.ArrayList<>(executionOrder);
        assertTrue("Execution order suggests lack of synchronization", orderList.size() == 6);

        // Test that method completes without flushing by writing more data
        for (int i = 10; i < 20; i++) {
            var state = Feature.singleState(FeatureId.INLINE_VECTORS, new InlineVectors.State(ravv.getVector(i % vectors.size())));
            writer.writeInline(i, state);
        }

        writer.write(Map.of());
    } catch (InterruptedException e) {
        Thread.currentThread().interrupt();
        throw new RuntimeException(e);
    }

    assertTrue(Files.exists(testPath));
}

