@Test
public void testInsertDiverseConcurrent() {
  // Test thread-local vector access using existing test structure
  var sf = VectorSimilarityFunction.DOT_PRODUCT;
  var vectors = new TestVectorGraph.CircularFloatVectorValues(10);
  var natural = new NodeArray(10);
  var concurrent = new NodeArray(10);
  var bsp = BuildScoreProvider.randomAccessScoreProvider(vectors, sf);

  // Test concurrent access to verify thread-local instances work correctly
  var threads = new Thread[2];
  var results = new boolean[2];

  for (int i = 0; i < 2; i++) {
    final int threadId = i;
    threads[i] = new Thread(() -> {
      try {
        // Each thread should get its own thread-local vector instance
        IntStream.range(0, 7)
            .forEach(j -> natural.insertSorted(j, scoreBetween(bsp, 7, j)));
        IntStream.range(8, 10)
            .forEach(j -> concurrent.insertSorted(j, scoreBetween(bsp, 7, j)));

        var cnm = new ConcurrentNeighborMap(new VamanaDiversityProvider(bsp, 1.0f), 10, 10);
        cnm.addNode(7);
        var neighbors = cnm.insertDiverse(7, NodeArray.merge(natural, concurrent));

        // Verify expected behavior with thread-local instances
        results[threadId] = (neighbors.size() == 2 && neighbors.contains(8) && neighbors.contains(6));
      } catch (Exception e) {
        results[threadId] = false;
      }
    });
  }

  // Start all threads and wait for completion
  for (Thread thread : threads) {
    thread.start();
  }

  for (Thread thread : threads) {
    try {
      thread.join();
    } catch (InterruptedException e) {
      Thread.currentThread().interrupt();
    }
  }

  // Verify all threads completed successfully using assert statements
  for (boolean result : results) {
    assert result;
  }
}

