public static long nextLong(Random random, long n) {
    if (n <= 0L) {
        throw new IllegalArgumentException("n must be positive");
    }
    if (n < Integer.MAX_VALUE) {
        return random.nextInt((int) n);
    }

    long bits;
    long val;
    do {
        bits = (random.nextLong() << 1) >>> 1;
        val = bits % n;
    } while (bits - val + (n - 1L) < 0L);
    return val;
}

