public @NonNull String summarize() {
    StringBuilder summary = new StringBuilder();
    summary.append("Explanation of score (%s):\n".formatted(score));
    summary.append("    Constraint matches:\n");

    constraintAnalyses().stream()
            .sorted(comparing(ConstraintAnalysis::score))
            .forEach(constraint -> {
                var matches = constraint.matches();
                if (matches == null) {
                    throw new IllegalArgumentException(
                            "The constraint matches must be non-null.\n" +
                            "Maybe use ScoreAnalysisFetchPolicy.FETCH_ALL to request the score analysis");
                }
                if (matches.isEmpty()) {
                    summary.append(
                            "        %s: constraint (%s) has no matches.\n".formatted(
                                    constraint.score().toShortString(),
                                    constraint.constraintRef().constraintName()));
                } else {
                    summary.append(
                            "        %s: constraint (%s) has %s matches:\n".formatted(
                                    constraint.score().toShortString(),
                                    constraint.constraintRef().constraintName(), 
                                    matches.size()));
                    matches.stream()
                            .sorted(comparing(MatchAnalysis::score))
                            .forEach(match -> summary
                                    .append("            %s: justified with (%s)\n".formatted(
                                            match.score().toShortString(),
                                            match.justification())));
                }
            });

    return summary.toString();
}

