public static String camelToUnderline(String string) {
    if (string == null || string.isEmpty()) {
        return "";
    }
    StringBuilder sb = new StringBuilder();
    for (int i = 0; i < string.length(); i++) {
        char c = string.charAt(i);
        if (Character.isUpperCase(c) && i > 0) {
            char prev = string.charAt(i - 1);
            // Insert underscore if previous char is lowercase
            if (Character.isLowerCase(prev)) {
                sb.append('_');
            }
            // Insert underscore if previous char is uppercase and current char starts a new word
            // This happens when we have consecutive uppercase letters followed by a mixed case word
            else if (Character.isUpperCase(prev) && i < string.length() - 1) {
                char next = string.charAt(i + 1);
                if (Character.isLowerCase(next)) {
                    sb.append('_');
                }
            }
        }
        sb.append(Character.toLowerCase(c));
    }
    return sb.toString();
}

