    String testConsecutiveUpper = "XMLParser";
    String result = StringUtil.camelToUnderline(testConsecutiveUpper);
    Assert.assertEquals("xml_parser", result);

    // Test uppercase after lowercase - should insert underscore
    String testLowerToUpper = "camelCase";
    String result2 = StringUtil.camelToUnderline(testLowerToUpper);
    Assert.assertEquals("camel_case", result2);

    // Test mixed case: should handle consecutive uppercase followed by mixed case
    String testMixed = "parseXMLData";
    String result3 = StringUtil.camelToUnderline(testMixed);
    Assert.assertEquals("parse_xml_data", result3);
}

